import React from "react";
import "../style/AllPosts.css";

const posts = [
  { id: 1, title: "Exploring React Components",
   content: "Learn how to build reusable components in React.",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_ylBuH0BTa7muk4RKc0cNJUVsH9kCPiyWPw&s"
   },
  { id: 2, title: "Understanding State and Props",
   content: "Master state and props for better component management.",
    image: "data:image/jpeg;base64,"
   },
  { id: 3, title: "JavaScript ES6 Features",
   content: "Dive into the essential features of modern JavaScript.",
    image: "data:image/jpeg;base64," 
  },
  { id: 4, title: "Tips for Clean Code in React",
   content: "Keep your React code maintainable and clean.",
    image: "https://miro.medium.com/v2/resize:fit:1400/0*FLGdKF6OE_cLes6A.jpg"
   },
  { id: 5, title: "The Basics of API Integration",
   content: "Connect your React app to REST APIs effortlessly.",
    image: "data:image/jpeg;base64,"
  },
  { id: 6, title: "CSS Styling Techniques",
   content: "Make your UI visually appealing with advanced CSS.",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQJdnf2Oz9hB0_rUiUlGByqkdJLrkA6M9dNrQ&s"
   },
  { id: 7, title: "Deploying React Apps",
   content: "Deploy your project easily on modern platforms.",
    image: "data:image/jpeg;base64,"
   },
  { id: 8, title: "Understanding React Lifecycle",
   content: "Handle side effects with lifecycle methods.",
    image: "data:image/png;base64,"
  },
  { id: 9, title: "Working with React Hooks",
   content: "Simplify your logic using useState and useEffect.",
    image: "data:image/png;base64,"
  },
  { id: 10, title: "Optimizing Performance in React",
   content: "Improve speed and responsiveness of your app.", 
   image: "data:image/jpeg;base64," 
  },
];

function AllPosts() {
  return (
    <div className="post-container">
      
      {posts.map((post) => (
        <article key={post.id} className="post-item">
          <img src={post.image} alt={post.title} className="post-image" />
          <h2 className="post-title">{post.title}</h2>
          <p className="post-content">{post.content}</p>
        </article>
      ))}
    </div>
  );
}

export default AllPosts;
