import React from "react";
import "../style/Article.css";

const articles = [
  { id: 1, title: "The Evolution of JavaScript"
  , content: "Explore the journey of JavaScript from ES5 to ES2025.",
   image: "data:image/jpeg;base64," },
  { id: 2, title: "React vs Angular vs Vue", content: "Comparing the top three front-end frameworks.", image: "data:image/png;base64," },
  { id: 3, title: "The Future of AI in Web Development", content: "How AI is revolutionizing web development.", image: "data:image/jpeg;base64," },
  { id: 4, title: "Accessibility in Modern Web Design", content: "Making web applications inclusive for everyone.", image: "data:image/jpeg;base64," },
  { id: 5, title: "The Rise of Progressive Web Apps", content: "Why PWAs are the future of mobile web.", image: "data:image/png;base64," },
  { id: 6, title: "Understanding GraphQL", content: "A beginner's guide to API querying with GraphQL.", image: "data:image/jpeg;base64," },
  { id: 7, title: "SEO Best Practices for 2025", content: "Optimize your website for search engines.", image: "data:image/jpeg;base64," },
  { id: 8, title: "Web Performance Optimization Tips", content: "Improve loading speed for your website.", image: "data:image/png;base64," },
  { id: 9, title: "Exploring WebAssembly", content: "A new way to run high-performance applications on the web.", image: "data:image/jpeg;base64," },
  { id: 10, title: "The Importance of Cybersecurity", content: "Keep your web applications safe from threats.", image: "data:image/jpeg;base64," },
];

function Article() {
  return (
    <div className="article-container">
      
      {articles.map((article) => (
        <article key={article.id} className="article-item">
          <img src={article.image} alt={article.title} className="article-image" />
          <h2 className="article-title">{article.title}</h2>
          <p className="article-content">{article.content}</p>
        </article>
      ))}
    </div>
  );
}

export default Article;
